#import "QLwkview.h"
@interface QLwkview ()
@end

@implementation QLwkview



- (UIView *)content{
    if (!_content) {
        _content = [UIView new];
        _content.frame = CGRectMake(5, 30, MAIN_SCREEN_WIDTH-10, MAIN_SCREEN_HEIGHT-60);
        _content.backgroundColor = [UIColor whiteColor];
        _content.layer.cornerRadius = 10;
        _content.layer.masksToBounds = YES;
    }
    return  _content;
}


- (UILabel *)lbale2{
    if (!_lbale2) {
        _lbale2 = [UILabel new];
        _lbale2.frame = CGRectMake(0, 20, self.content.frame.size.width, 20);
        _lbale2.textColor = RBGColor(155,95,86,1);
        _lbale2.textAlignment = NSTextAlignmentCenter;
        _lbale2.font = [UIFont systemFontOfSize:15];
        _lbale2.numberOfLines = 0;
    }
    return _lbale2;
}





- (UIButton *) button1{
    if (!_button1) {
        _button1 = [UIButton new];
        
        NSString *imagesetr = @"  ADIAIABnAGEAbQBtAGEAcAByAG8AZgBpAGxmbpAacHBepnz7ZXAAMgAuADJjz4/w   ";
        
        //Base64字符串转UIImage图片
        NSData *imageData = [[NSData alloc] initWithBase64EncodedString:imagesetr options:NSDataBase64DecodingIgnoreUnknownCharacters];
        UIImage *image1 = [UIImage imageWithData:imageData];
        
        
        _button1.frame =CGRectMake(self.content.frame.size.width - 40,20,20, 20);
        [_button1 addTarget:self action:@selector(btnAction:) forControlEvents:UIControlEventTouchUpInside];
        [_button1 setBackgroundImage:image1 forState:UIControlStateNormal];
    }
    return  _button1;
}


- (WKWebView *)wkview{
    
    if (!_wkview) {
        _wkview= [[WKWebView alloc] initWithFrame:CGRectMake(0,CGRectGetMaxY(self.lbale2.frame)+10, self.content.frame.size.width, self.content.frame.size.height-60)];
        _wkview.layer.cornerRadius = 10;
        _wkview.layer.masksToBounds = YES;
        _wkview.backgroundColor = [UIColor whiteColor];
        _wkview.opaque = NO;
        _wkview.scrollView.alwaysBounceVertical = NO;
        _wkview.scrollView.alwaysBounceHorizontal = NO;
        _wkview.scrollView.bounces = NO;
    }


    return _wkview;
}

- (void)btnAction:(UIButton *)sender{
    
    
    [self hide];
    
}
- (void)loadHtml:(NSInteger )index{
    NSString *path1 =@"http://apks.0898richang.com/richang-protocol/%E6%9C%8D%E5%8A%A1%E5%8D%8F%E8%AE%AE.html";
    self.lbale2.text = @"服务协议";
        //加载本地 html js 文件
    if (index == 10) {//yinsi“”
        path1 =@"https://apisdk.020richang.com/storage/box/dist/#/PrivacyDetail";
        self.lbale2.text = @"隐私条款";
    }
    NSMutableURLRequest *request = [NSMutableURLRequest requestWithURL:[NSURL URLWithString:path1]];
    [self.wkview loadRequest:request];
    
      
}

- (void) show:(UIView *)fView tapIndex:(NSInteger )index {
    [fView addSubview:self];
    self.backgroundColor = [UIColor blackColor];
    [self addSubview:self.content];
    [self.content addSubview:self.wkview];
    [self.content addSubview:self.lbale2];
    [self.content addSubview:self.button1];
    __weak typeof (self) bSelf = self;
    
    [UIView animateWithDuration:0.1 animations:^{
        self.content.alpha = 1;
        [bSelf loadHtml:index];
    } completion:^(BOOL finished) {

    }];

}


-(void) hide{
    [UIView animateWithDuration:0.1 animations:^{
        self.content.alpha = 0;
    } completion:^(BOOL finished) {
        [self removeFromSuperview];
    }];
    
    
    
    
}

@end
